import{_ as e,o as a,c as l,ag as d}from"./chunks/framework.BUWCGn8t.js";const f=JSON.parse('{"title":"Tachikoma vs Ratatui","description":"","frontmatter":{},"headers":[],"relativePath":"comparison.md","filePath":"comparison.md","lastUpdated":null}'),i={name:"comparison.md"};function o(c,t,s,r,n,h){return a(),l("div",null,[...t[0]||(t[0]=[d('<h1 id="Tachikoma-vs-Ratatui" tabindex="-1">Tachikoma vs Ratatui <a class="header-anchor" href="#Tachikoma-vs-Ratatui" aria-label="Permalink to &quot;Tachikoma vs Ratatui {#Tachikoma-vs-Ratatui}&quot;">​</a></h1><p>Tachikoma.jl and <a href="https://ratatui.rs/" target="_blank" rel="noreferrer">Ratatui</a> share the immediate-mode rendering model and constraint-based layouts. This page highlights similarities and differences.</p><h2 id="architecture" tabindex="-1">Architecture <a class="header-anchor" href="#architecture" aria-label="Permalink to &quot;Architecture&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">Feature</th><th style="text-align:left;">Tachikoma.jl</th><th style="text-align:left;">Ratatui</th></tr></thead><tbody><tr><td style="text-align:left;">Language</td><td style="text-align:left;">Julia</td><td style="text-align:left;">Rust</td></tr><tr><td style="text-align:left;">Rendering model</td><td style="text-align:left;">Immediate mode</td><td style="text-align:left;">Immediate mode</td></tr><tr><td style="text-align:left;">App pattern</td><td style="text-align:left;">Elm architecture (Model → update! → view)</td><td style="text-align:left;">User-managed event loop</td></tr><tr><td style="text-align:left;">State management</td><td style="text-align:left;">Mutable model struct</td><td style="text-align:left;">User-managed</td></tr><tr><td style="text-align:left;">Event loop</td><td style="text-align:left;">Built-in <code>app()</code> with configurable FPS</td><td style="text-align:left;">Manual (crossterm/termion)</td></tr><tr><td style="text-align:left;">Default bindings</td><td style="text-align:left;">Built-in (theme, settings, help overlays)</td><td style="text-align:left;">None</td></tr></tbody></table><h2 id="Layout-System" tabindex="-1">Layout System <a class="header-anchor" href="#Layout-System" aria-label="Permalink to &quot;Layout System {#Layout-System}&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">Feature</th><th style="text-align:left;">Tachikoma.jl</th><th style="text-align:left;">Ratatui</th></tr></thead><tbody><tr><td style="text-align:left;">Constraint types</td><td style="text-align:left;"><a href="/Tachikoma.jl/dev/api#Tachikoma.Fixed" class="tachi-api-link"><code>Fixed</code></a>, <a href="/Tachikoma.jl/dev/api#Tachikoma.Fill" class="tachi-api-link"><code>Fill</code></a>, <a href="/Tachikoma.jl/dev/api#Tachikoma.Percent" class="tachi-api-link"><code>Percent</code></a>, <a href="/Tachikoma.jl/dev/api#Tachikoma.Min" class="tachi-api-link"><code>Min</code></a>, <a href="/Tachikoma.jl/dev/api#Tachikoma.Max" class="tachi-api-link"><code>Max</code></a>, <a href="/Tachikoma.jl/dev/api#Tachikoma.Ratio" class="tachi-api-link"><code>Ratio</code></a></td><td style="text-align:left;"><code>Length</code>, <a href="/Tachikoma.jl/dev/api#Tachikoma.Fill" class="tachi-api-link"><code>Fill</code></a>, <code>Percentage</code>, <a href="/Tachikoma.jl/dev/api#Tachikoma.Min" class="tachi-api-link"><code>Min</code></a>, <a href="/Tachikoma.jl/dev/api#Tachikoma.Max" class="tachi-api-link"><code>Max</code></a>, <a href="/Tachikoma.jl/dev/api#Tachikoma.Ratio" class="tachi-api-link"><code>Ratio</code></a></td></tr><tr><td style="text-align:left;">Flex alignment</td><td style="text-align:left;"><code>layout_start</code>, <code>layout_center</code>, <code>layout_end</code>, <code>layout_space_between</code>, <code>layout_space_around</code>, <code>layout_space_evenly</code></td><td style="text-align:left;"><code>Flex::Start</code>, <code>Flex::Center</code>, <code>Flex::End</code>, <code>Flex::SpaceBetween</code>, <code>Flex::SpaceAround</code>, <code>Flex::SpaceEvenly</code></td></tr><tr><td style="text-align:left;">Spacing</td><td style="text-align:left;"><code>Layout(dir, constraints; spacing=n)</code></td><td style="text-align:left;"><code>Layout::default().spacing(n)</code></td></tr><tr><td style="text-align:left;">Resizable panes</td><td style="text-align:left;"><a href="/Tachikoma.jl/dev/layout#resizablelayout" class="tachi-api-link"><code>ResizableLayout</code></a> with mouse drag</td><td style="text-align:left;">Not built-in</td></tr></tbody></table><h2 id="widgets" tabindex="-1">Widgets <a class="header-anchor" href="#widgets" aria-label="Permalink to &quot;Widgets&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">Widget</th><th style="text-align:left;">Tachikoma.jl</th><th style="text-align:left;">Ratatui</th></tr></thead><tbody><tr><td style="text-align:left;">Block (borders)</td><td style="text-align:left;"><a href="/Tachikoma.jl/dev/widgets#block" class="tachi-api-link"><code>Block</code></a> with <code>BOX_ROUNDED</code>, <code>BOX_HEAVY</code>, etc.</td><td style="text-align:left;"><a href="/Tachikoma.jl/dev/widgets#block" class="tachi-api-link"><code>Block</code></a> with <code>BorderType</code></td></tr><tr><td style="text-align:left;">Paragraph</td><td style="text-align:left;"><a href="/Tachikoma.jl/dev/api#Tachikoma.Paragraph-Tuple{AbstractString}" class="tachi-api-link"><code>Paragraph</code></a> with <a href="/Tachikoma.jl/dev/widgets#paragraph" class="tachi-api-link"><code>Span</code></a></td><td style="text-align:left;"><a href="/Tachikoma.jl/dev/api#Tachikoma.Paragraph-Tuple{AbstractString}" class="tachi-api-link"><code>Paragraph</code></a> with <a href="/Tachikoma.jl/dev/widgets#paragraph" class="tachi-api-link"><code>Span</code></a></td></tr><tr><td style="text-align:left;">List</td><td style="text-align:left;"><a href="/Tachikoma.jl/dev/api#Tachikoma.SelectableList-Tuple{Vector{ListItem}}" class="tachi-api-link"><code>SelectableList</code></a> with mouse support</td><td style="text-align:left;"><code>List</code></td></tr><tr><td style="text-align:left;">Table</td><td style="text-align:left;"><a href="/Tachikoma.jl/dev/widgets#table" class="tachi-api-link"><code>Table</code></a>, <a href="/Tachikoma.jl/dev/widgets#datatable" class="tachi-api-link"><code>DataTable</code></a> (sortable, filterable)</td><td style="text-align:left;"><a href="/Tachikoma.jl/dev/widgets#table" class="tachi-api-link"><code>Table</code></a></td></tr><tr><td style="text-align:left;">Tabs</td><td style="text-align:left;"><a href="/Tachikoma.jl/dev/widgets#tabbar" class="tachi-api-link"><code>TabBar</code></a></td><td style="text-align:left;"><code>Tabs</code></td></tr><tr><td style="text-align:left;">Gauge</td><td style="text-align:left;"><a href="/Tachikoma.jl/dev/widgets#gauge" class="tachi-api-link"><code>Gauge</code></a> with animation tick</td><td style="text-align:left;"><a href="/Tachikoma.jl/dev/widgets#gauge" class="tachi-api-link"><code>Gauge</code></a>, <code>LineGauge</code></td></tr><tr><td style="text-align:left;">Sparkline</td><td style="text-align:left;"><a href="/Tachikoma.jl/dev/widgets#sparkline" class="tachi-api-link"><code>Sparkline</code></a></td><td style="text-align:left;"><a href="/Tachikoma.jl/dev/widgets#sparkline" class="tachi-api-link"><code>Sparkline</code></a></td></tr><tr><td style="text-align:left;">BarChart</td><td style="text-align:left;"><a href="/Tachikoma.jl/dev/widgets#barchart" class="tachi-api-link"><code>BarChart</code></a> with <code>BarEntry</code></td><td style="text-align:left;"><a href="/Tachikoma.jl/dev/widgets#barchart" class="tachi-api-link"><code>BarChart</code></a> with <code>Bar</code></td></tr><tr><td style="text-align:left;">Chart</td><td style="text-align:left;"><a href="/Tachikoma.jl/dev/widgets#chart" class="tachi-api-link"><code>Chart</code></a> with <a href="/Tachikoma.jl/dev/widgets#chart" class="tachi-api-link"><code>DataSeries</code></a> (line/scatter)</td><td style="text-align:left;"><a href="/Tachikoma.jl/dev/widgets#chart" class="tachi-api-link"><code>Chart</code></a> with <code>Dataset</code></td></tr><tr><td style="text-align:left;">Calendar</td><td style="text-align:left;"><a href="/Tachikoma.jl/dev/widgets#calendar" class="tachi-api-link"><code>Calendar</code></a></td><td style="text-align:left;"><code>Monthly</code> (ratatui-widgets)</td></tr><tr><td style="text-align:left;">Canvas</td><td style="text-align:left;"><a href="/Tachikoma.jl/dev/canvas#Canvas-Braille" class="tachi-api-link"><code>Canvas</code></a>, <a href="/Tachikoma.jl/dev/canvas#BlockCanvas-Quadrant-Blocks" class="tachi-api-link"><code>BlockCanvas</code></a>, <a href="/Tachikoma.jl/dev/canvas#PixelCanvas-Pixel-Drawing-Surface" class="tachi-api-link"><code>PixelCanvas</code></a></td><td style="text-align:left;"><a href="/Tachikoma.jl/dev/canvas#Canvas-Braille" class="tachi-api-link"><code>Canvas</code></a></td></tr><tr><td style="text-align:left;">Scrollbar</td><td style="text-align:left;"><a href="/Tachikoma.jl/dev/widgets#scrollbar" class="tachi-api-link"><code>Scrollbar</code></a>, <a href="/Tachikoma.jl/dev/widgets#scrollpane" class="tachi-api-link"><code>ScrollPane</code></a></td><td style="text-align:left;"><a href="/Tachikoma.jl/dev/widgets#scrollbar" class="tachi-api-link"><code>Scrollbar</code></a></td></tr><tr><td style="text-align:left;">BigText</td><td style="text-align:left;"><a href="/Tachikoma.jl/dev/widgets#bigtext" class="tachi-api-link"><code>BigText</code></a></td><td style="text-align:left;"><a href="/Tachikoma.jl/dev/widgets#bigtext" class="tachi-api-link"><code>BigText</code></a> (ratatui-widgets)</td></tr><tr><td style="text-align:left;">Modal</td><td style="text-align:left;"><a href="/Tachikoma.jl/dev/widgets#modal" class="tachi-api-link"><code>Modal</code></a></td><td style="text-align:left;">Not built-in</td></tr><tr><td style="text-align:left;">Form</td><td style="text-align:left;"><a href="/Tachikoma.jl/dev/api#Tachikoma.Form-Tuple{Vector{FormField}}" class="tachi-api-link"><code>Form</code></a> with <a href="/Tachikoma.jl/dev/widgets#Form-/-FormField" class="tachi-api-link"><code>FormField</code></a></td><td style="text-align:left;">Not built-in</td></tr><tr><td style="text-align:left;">TreeView</td><td style="text-align:left;"><a href="/Tachikoma.jl/dev/api#Tachikoma.TreeView-Tuple{TreeNode}" class="tachi-api-link"><code>TreeView</code></a> with <a href="/Tachikoma.jl/dev/widgets#TreeView-/-TreeNode" class="tachi-api-link"><code>TreeNode</code></a></td><td style="text-align:left;"><code>Tree</code> (tui-tree-widget)</td></tr></tbody></table><h2 id="Tachikoma-Only-Features" tabindex="-1">Tachikoma-Only Features <a class="header-anchor" href="#Tachikoma-Only-Features" aria-label="Permalink to &quot;Tachikoma-Only Features {#Tachikoma-Only-Features}&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">Feature</th><th style="text-align:left;">Description</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>Input widgets</strong></td><td style="text-align:left;"><a href="/Tachikoma.jl/dev/api#Tachikoma.TextInput-Tuple{}" class="tachi-api-link"><code>TextInput</code></a>, <a href="/Tachikoma.jl/dev/api#Tachikoma.TextArea-Tuple{}" class="tachi-api-link"><code>TextArea</code></a>, <a href="/Tachikoma.jl/dev/api#Tachikoma.CodeEditor-Tuple{}" class="tachi-api-link"><code>CodeEditor</code></a>, <a href="/Tachikoma.jl/dev/api#Tachikoma.Checkbox-Tuple{String}" class="tachi-api-link"><code>Checkbox</code></a>, <a href="/Tachikoma.jl/dev/api#Tachikoma.RadioGroup-Tuple{Vector{String}}" class="tachi-api-link"><code>RadioGroup</code></a>, <a href="/Tachikoma.jl/dev/api#Tachikoma.DropDown-Tuple{Vector{String}}" class="tachi-api-link"><code>DropDown</code></a></td></tr><tr><td style="text-align:left;"><strong>Form system</strong></td><td style="text-align:left;"><a href="/Tachikoma.jl/dev/api#Tachikoma.Form-Tuple{Vector{FormField}}" class="tachi-api-link"><code>Form</code></a> / <a href="/Tachikoma.jl/dev/widgets#Form-/-FormField" class="tachi-api-link"><code>FormField</code></a> with focus navigation and validation</td></tr><tr><td style="text-align:left;"><strong>FocusRing</strong></td><td style="text-align:left;">Automatic Tab/Shift-Tab navigation between widgets</td></tr><tr><td style="text-align:left;"><strong>Animation system</strong></td><td style="text-align:left;"><a href="/Tachikoma.jl/dev/animation#tweens" class="tachi-api-link"><code>Tween</code></a>, <a href="/Tachikoma.jl/dev/api#Tachikoma.Spring-Tuple{Real}" class="tachi-api-link"><code>Spring</code></a>, <a href="/Tachikoma.jl/dev/animation#timelines" class="tachi-api-link"><code>Timeline</code></a> (<code>sequence</code>, <code>stagger</code>, <code>parallel</code>)</td></tr><tr><td style="text-align:left;"><strong>Organic effects</strong></td><td style="text-align:left;"><code>pulse</code>, <code>breathe</code>, <code>shimmer</code>, <code>noise</code>, <code>fbm</code>, <code>color_wave</code></td></tr><tr><td style="text-align:left;"><strong>Buffer fills</strong></td><td style="text-align:left;"><code>fill_gradient!</code>, <code>fill_noise!</code>, <code>border_shimmer!</code></td></tr><tr><td style="text-align:left;"><strong>Backgrounds</strong></td><td style="text-align:left;"><a href="/Tachikoma.jl/dev/backgrounds#dotwavebackground" class="tachi-api-link"><code>DotWaveBackground</code></a>, <a href="/Tachikoma.jl/dev/backgrounds#phylotreebackground" class="tachi-api-link"><code>PhyloTreeBackground</code></a>, <a href="/Tachikoma.jl/dev/backgrounds#cladogrambackground" class="tachi-api-link"><code>CladogramBackground</code></a></td></tr><tr><td style="text-align:left;"><strong>Pixel graphics</strong></td><td style="text-align:left;"><a href="/Tachikoma.jl/dev/api#Tachikoma.PixelImage-Tuple{Int64, Int64}" class="tachi-api-link"><code>PixelImage</code></a>, <a href="/Tachikoma.jl/dev/canvas#PixelCanvas-Pixel-Drawing-Surface" class="tachi-api-link"><code>PixelCanvas</code></a> for pixel-perfect raster rendering (Kitty or sixel)</td></tr><tr><td style="text-align:left;"><strong>Theme system</strong></td><td style="text-align:left;">11 built-in themes (<code>kokaku</code>, <code>esper</code>, <code>motoko</code>, etc.) with live switching</td></tr><tr><td style="text-align:left;"><strong>Settings overlay</strong></td><td style="text-align:left;">Ctrl+S opens in-app settings (theme, animations, backgrounds)</td></tr><tr><td style="text-align:left;"><strong>Async tasks</strong></td><td style="text-align:left;"><a href="/Tachikoma.jl/dev/async#Setting-Up" class="tachi-api-link"><code>TaskQueue</code></a> / <code>spawn_task!</code> for non-blocking background work</td></tr><tr><td style="text-align:left;"><strong>MarkdownPane</strong></td><td style="text-align:left;">CommonMark rendering with scroll support</td></tr><tr><td style="text-align:left;"><strong>Pattern matching</strong></td><td style="text-align:left;"><code>@match</code> integration for event handling</td></tr><tr><td style="text-align:left;"><strong>Recording &amp; export</strong></td><td style="text-align:left;"><code>record_app</code>, <code>record_widget</code> → <code>.tach</code>, <code>.svg</code>, <code>.gif</code></td></tr><tr><td style="text-align:left;"><strong>ProgressList</strong></td><td style="text-align:left;">Task status list with <code>task_done</code>, <code>task_running</code>, <code>task_pending</code></td></tr><tr><td style="text-align:left;"><strong>Container</strong></td><td style="text-align:left;">Automatic widget layout positioning</td></tr><tr><td style="text-align:left;"><strong>StatusBar</strong></td><td style="text-align:left;">Full-width bar with left/right aligned spans</td></tr></tbody></table><h2 id="Ratatui-Only-Features" tabindex="-1">Ratatui-Only Features <a class="header-anchor" href="#Ratatui-Only-Features" aria-label="Permalink to &quot;Ratatui-Only Features {#Ratatui-Only-Features}&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">Feature</th><th style="text-align:left;">Description</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>Rust ecosystem</strong></td><td style="text-align:left;">Access to the full Rust crate ecosystem</td></tr><tr><td style="text-align:left;"><strong>Performance</strong></td><td style="text-align:left;">Zero-cost abstractions, no GC pauses</td></tr><tr><td style="text-align:left;"><strong>Backend choice</strong></td><td style="text-align:left;">Crossterm, termion, or termwiz backends</td></tr><tr><td style="text-align:left;"><strong>Widget crates</strong></td><td style="text-align:left;">Large ecosystem of community widgets</td></tr><tr><td style="text-align:left;"><strong>Color types</strong></td><td style="text-align:left;"><code>Color::Rgb</code>, <code>Color::Indexed</code>, <code>Color::Reset</code></td></tr><tr><td style="text-align:left;"><strong>Modifiers</strong></td><td style="text-align:left;">Fine-grained text modifiers (<code>BOLD</code>, <code>ITALIC</code>, <code>UNDERLINED</code>, etc.)</td></tr><tr><td style="text-align:left;"><strong>Masked input</strong></td><td style="text-align:left;">Built-in password/masked text input</td></tr></tbody></table><h2 id="Migration-Guide" tabindex="-1">Migration Guide <a class="header-anchor" href="#Migration-Guide" aria-label="Permalink to &quot;Migration Guide {#Migration-Guide}&quot;">​</a></h2><p>If you&#39;re coming from Ratatui to Tachikoma:</p><table tabindex="0"><thead><tr><th style="text-align:left;">Ratatui</th><th style="text-align:left;">Tachikoma.jl</th></tr></thead><tbody><tr><td style="text-align:left;"><code>Frame::render_widget(w, area)</code></td><td style="text-align:left;"><code>render(widget, area, buf)</code></td></tr><tr><td style="text-align:left;"><code>Layout::default().constraints([...]).split(area)</code></td><td style="text-align:left;"><code>split_layout(Layout(dir, [...]), area)</code></td></tr><tr><td style="text-align:left;"><code>Paragraph::new(vec![Span::styled(...)])</code></td><td style="text-align:left;"><code>Paragraph([Span(&quot;...&quot;, style)])</code></td></tr><tr><td style="text-align:left;"><code>Block::default().title(&quot;...&quot;).borders(Borders::ALL)</code></td><td style="text-align:left;"><code>Block(title=&quot;...&quot;, border_style=tstyle(:border))</code></td></tr><tr><td style="text-align:left;"><code>ListState { selected: Some(i) }</code></td><td style="text-align:left;"><code>SelectableList(...; selected=i)</code></td></tr><tr><td style="text-align:left;"><code>event::read()?</code></td><td style="text-align:left;"><code>update!(model, evt)</code> callback</td></tr><tr><td style="text-align:left;"><code>Terminal::draw(|f| { ... })?</code></td><td style="text-align:left;"><code>view(model, frame)</code> callback</td></tr></tbody></table>',15)])])}const y=e(i,[["render",o]]);export{f as __pageData,y as default};
